mapper.select:
select bis.id,bis.in_num,bis.type,bis.operator,bis.create_time,bis.modified,bis.product_number,
bis.supplier_id,bis.remark,bis.status,bs.phone
from biz_in_stock bis,biz_supplier bs


        and status=#{status}


        and type=#{type}


        and inNum=#{inNum}



        and bis.gmt_create>=#{startTime}


        and bis.gmt_create <= #{endTime}

    and bis.supplier_id=bs.id

select * from biz_in_stock


        and id=#{id}


        and username=#{username}
