mapper.update:
update biz_product
set status = 0
where id = #{id}

mapper.select:
select id, p_num, name, image_url, model, unit, remark, sort, create_time, modified_time, one_category_id, two_category_id, three_category_id, status from biz_product
and one_category_id=#{oneCategoryId}
and two_category_id=#{twoCategoryId}
and three_category_id=#{threeCategoryId}
and name like '%' #{name} '%'
and status=#{status}

SELECT s.p_num, s.stock, p.name, p.id, p.model, p.unit, p.image_url FROM biz_product_stock s , biz_product p
s.`p_num` = p.`p_num` and s.`stock` > 0
and one_category_id=#{oneCategoryId}
and two_category_id=#{twoCategoryId}
and three_category_id=#{threeCategoryId}
and name like '%' #{name} '%'
and status=#{status}

select id, p_num, name, image_url, model, unit, remark, sort, create_time, modified_time, one_category_id, two_category_id, three_category_id, status from biz_product
and one_category_id=#{oneCategoryId}
and two_category_id=#{twoCategoryId}
and three_category_id=#{threeCategoryId}
and name like '%' #{name} '%'
and status=#{status}

mapper.update:
update biz_product
set status = 0
where id = #{id}

update biz_product
set status = 1
where id = #{id}
